import axios from 'axios';
import PropTypes from 'prop-types';
import React, { Component, Fragment } from 'react';
import ReactDOM from 'react-dom';
import Categories from './Categories';
import { Header, Footer } from './Layouts/index'; 
import { food_categories, foodItems, positions } from '../store.js';
import { connect } from 'tls';


export default class AppPage extends Component {
    state = {
        food_category: 'Beef',
        restaurants: [{"storeID":1,"name":null,"address":null,"phone":null,"createdAt":"2019-11-09T22:17:57.000Z","updatedAt":"2019-11-09T22:17:57.000Z"}],
        err: ''
    }

    componentDidMount() {
        //this.props.fetchRestaurants();
        
        this.setState({err:'NO ERROR'});
        this.fetchRestaurants();
    }

    fetchRestaurants = () => {
        fetch('http://localhost:3001/allrest')
            .then((response) => response.json())
            .then((data) => {
                this.setState({ restaurants:data });
            });

        // axios
        //     .get('http://localhost:3001/allrest')
        //     .then( response => {
        //         const rests = response.data;
        //         this.setState({ restaurants:rests });
        //     })
        //     .catch( error => {
        //         console.log(error);
        //         this.setState( {err:'SOMETHING WENT WRONG'} );
        //         return null;
        //     });

        // let response = await axios.get('http://localhost:3001/allrest');
        // let {rests} = response.data;
        // this.setState({ restaurants:rests});
    }

    //method to get foodItems based on their category
    getItemsByCategories() {
        return Object.entries(
            this.state.foodItems.reduce((foodItems, item) => {
                const { food_categories } = item

                foodItems[food_categories] = foodItems[food_categories]
                    ? [...foodItems[food_categories], item]
                    : [item]

                return foodItems    
            }, {})
        )
    }

    handleCategorySelected = food_categories => {
        this.setState(
            {food_category: food_categories}
        )
    }

    handlePositionChosen = positions => {
        this.setState(
            {positions: positions}
        )
    }

    render() {
        //const foodItems = this.getItemsByCategories()
        const {food_category} = this.state;
        return <Fragment>
            <Header
              foodItems={foodItems}
              onSelect={this.handleCategorySelected}
              food_category={food_category}
            />
            <ul>
                Something:
                {axios.get('http://192.168.233.1:3001/allrest').data}
                Please.
            </ul>
            <ul>
                {this.state.restaurants.map(rest => <li>{rest.storeID}</li>)}
            </ul>
            <ul>
                {this.state.err}
            </ul>
            <Categories
                foodItems={foodItems}
                food_category={food_category}
            />
            <Footer positions = { positions }
                    onSelect = {this.handlePositionChosen} 
            />
        </Fragment>
    }
}

AppPage.propTypes = {
    restaurants: PropTypes.array.isRequired,
    fetchRestaurants: PropTypes.func.isRequired
}

