import axios from 'axios';
import React, { Component, Fragment } from 'react';
import Categories from './Categories';
import { Header, Footer } from './Layouts/index'; 
import { foodItems, positions } from '../store.js';


export default class AppPage extends Component {
    state = {
        food_category: 'Beef',
        posIndex: 0,
        restaurants: [],
        restaurantID: 0,
        restaurant: {}
    }

    componentDidMount() {
        this.fetchRestaurants();

        // Setting state restaurantID to the first in the list's ID
        //this.setState({restaurant:this.state.restaurants[0]});
        console.log(this.state.restaurants);

        this.getCurrentRest();
    }

    fetchRestaurants = () => {
        axios
            .get('http://localhost:12000/allrest')
            .then( response => {
                this.setState({ restaurants:response.data });
                console.log(response.data);
            })
            .catch( error => {
                console.log(error);
            });
    }

    getCurrentRest = () => {
        const restIndex = this.state.restaurantID;
        console.log("REST INDEX: " + restIndex);
        axios
            .get("http://localhost:12000/rest/" + restIndex)
            .then( response => {
                console.log("***DEBUG MSG - getCurrentRest: " + response.data);
                this.setState({ restaurant:response.data });
            })
            .catch( error => {
                console.log(error);
            });
    }

    //method to get foodItems based on their category
    getItemsByCategories() {
        return Object.entries(
            this.state.foodItems.reduce((foodItems, item) => {
                const { food_categories } = item

                foodItems[food_categories] = foodItems[food_categories]
                    ? [...foodItems[food_categories], item]
                    : [item]

                return foodItems    
            }, {})
        )
    }

    handleCategorySelected = food_categories => {
        this.setState(
            {food_category: food_categories}
        )
    }

    handlePositionChosen = index => {
        this.setState(
            { posIndex: index }
        )
    }

    render() {
        //const foodItems = this.getItemsByCategories()
        const {food_category} = this.state;
        const {posIndex} = this.state;
        return <Fragment>
            <Header
                foodItems = { foodItems }
                onSelectCat = { this.handleCategorySelected }
                food_category = { food_category }
                positions = { positions }
                posIndex = { posIndex }
                onSelectPos = { this.handlePositionChosen }
            />
            <ul>
                {this.state.restaurant.storeID}
            </ul>
            <ul>
                {this.state.restaurants.map(rest => <li>{rest.createdAt}</li>)}
            </ul>
            <Categories
                foodItems={foodItems}
                food_category={food_category}
                posIndex = { posIndex }
            />
            <Footer />
        </Fragment>
    }
}
