import React, { Component, Fragment } from 'react';
import ReactDOM from 'react-dom';
import Categories from './Categories';
import { Header, Footer } from './Layouts/index'; 
import { food_category, foodItems } from '../store.js';

export default class extends Component {
    states = {
        foodItems
    }

    //method to get foodItems based on their category
    getItemsByCategories() {
        return Object.entries(
            this.state.foodItems.reduce((foodItems, item) => {
                const { food_category } = item

                foodItems[food_category] = foodItems[food_category]
                    ? [...foodItems[food_category], item]
                    : [item]

                return foodItems    
            }, {})
        )
    }

    render() {
        //const footItems = this.getItemsByCategories()
        return <Fragment>
            <Header />
            <Categories
                foodItems={foodItems}
            />
            <Footer food_category = { food_category }/>
        </Fragment>
    }
}
