import React, { Fragment, state } from 'react';
import {Grid, Paper, Typography, IconButton } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import 'typeface-roboto';
import GridList from '@material-ui/core/GridList';
import { mergeClasses } from '@material-ui/styles';


const styles = {
    Paper: { padding: 20, marginTop: 10, marginBottom: 10}
}

export default ({foodItems, food_category, posIndex}) =>

    {
        state = {
            shoppingCart: [{name:"Galbi"},{name:"Trash"}]
        }

        const handleCartAdd = item => {
            this.state.shoppingCart.push(item);
            console.log("DEBUG MSG: calling handleCartAdd for item: " + item.name);
        };

        if(posIndex === 0) {
            return (
                <Fragment>
                    <Typography variant="h1">
                        {food_category}
                    </Typography>
                    <Grid container spacing={3}>
                        <Grid item xs>
                            <div className={mergeClasses.root}>
                                <GridList
                                    className={mergeClasses.gridList}
                                    cols={4} >

                                    {foodItems.map( ( foodItem ) =>
                                        { if (foodItem.food_category === food_category) {
                                            return <Typography >
                                                        <Paper style={styles.Paper}>
                                                            {foodItem.name}
                                                            <div/>
                                                            {foodItem.description}
                                                            <div/>
                                                            <IconButton
                                                                size="small"
                                                                color="secondary"
                                                                aria-label="add"
                                                                onClick={() => { handleCartAdd(foodItem); }}>
                                                                <AddIcon />
                                                            </IconButton>
                                                        </Paper>
                                                    </Typography>
                                            }
                                        }   
                                    )}

                                </GridList>
                            </div>
                        </Grid>

                        <Grid item xs={3}>

                            <Paper style={styles.Paper}>
                                Shopping Cart
                                <div/>
                                <GridList className={mergeClasses.gridList} cols={1}>
                                    {shoppingCart.map( ( item ) => 
                                        { return <Typography>
                                                    {item.name}
                                                    <IconButton aria-label="delete">
                                                        <DeleteIcon />
                                                    </IconButton>
                                                </Typography>
                                        }
                                    )}
                                </GridList>
                            </Paper>
                        </Grid>
                    </Grid>
                </Fragment>
            )
        }
        else if( posIndex === 1 ) {
            return (
                <Grid>
                    Staff Page
                </Grid>
            )
        }
        else {
            return (
                <Grid>
                    Admin Page
                </Grid>
            )
        }
    }
